import { useState, useEffect } from "react";
import axios from "axios";

function useFlip(initialState = true) {
    const [flipped, setFlipped] = useState(initialState);

    const flip = () => {
        setFlipped(isUp => !isUp);
    };

    return [flipped, flip]
}

export { useFlip }
